import React, { Component } from "react";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";

export default class GameControls extends Component {
  constructor() {
    super();
    this.forfeit = this.forfeit.bind(this);
  }
  componentDidMount() {
    this.setState = {
      gameid: this.props.gameid,
      winner: this.props.player
    };
  }

  forfeit() {
    fetch("/game/" + this.props.gameid, {
      method: "post",
      body: {
        winner: this.props.player,
        replay: false
      }
    })
      .then(response => response.json())
      .then(res => {
        alert(res);
      });
  }

  render() {
    return (
      <div className="gamecontrols">
        <Row>
          <Col>
            <h1>{this.props.turn}</h1>
          </Col>
          <Col className="justify-content-end">
            <Button
              className="btn-danger"
              type="submit"
              onClick={e => {
                if (window.confirm("Are you sure you wish to forfeit?"))
                  this.forfeit(e);
              }}
            >
              Forfeit Game
            </Button>
          </Col>
        </Row>
      </div>
    );
  }
}
