import Chessboard from "chessboardjsx";
import React, { Component } from "react";
import Chess from "chess.js";
import { retrieveCookie } from "./cookies";

import io from "socket.io-client";

class Board extends Component {
  constructor(props) {
    super(props);
    this.state = {
      orientation: "black",
      game: new Chess(),
      socket: io.connect(
        "http://ec2-13-58-209-86.us-east-2.compute.amazonaws.com:8080/game/" +
          this.props.url
      )
    };
    this.updateState = this.updateState.bind(this);
    this.stateReceiver = this.stateReceiver.bind(this);
    this.receiveState = this.receiveState.bind(this);
  }

  state = {
    game: new Chess()
  };

  updateState(gameState) {
    this.state.socket.emit("subscribeToState", gameState);
  }

  stateReceiver(receieveState) {
    this.state.socket.on("state", receieveState);
  }

  receiveState(state) {
    var newChess = Chess();
    newChess.load_pgn(state);

    this.setState(
      {
        game: newChess
      },
      () => console.log()
    );
  }

  componentDidMount() {
    this.stateReceiver(this.receiveState);
    var orientation;

    //board facing player
    if (this.props.hostplayerid === retrieveCookie()) {
      orientation = "white";
    } else {
      orientation = "black";
    }

    fetch(
      "/game/" +
        this.props.gamesessionid +
        "/" +
        this.props.hostplayerid +
        "/" +
        this.props.playerid
    )
      .then(response => response.json())
      .then(res => {
        var newChess = Chess();
        newChess.load_pgn(res.pgn);
        newChess.header("White", this.props.hostplayerid);
        newChess.header("Black", this.props.playerid);

        this.setState(
          {
            game: newChess
          },
          () => console.log()
        );
        this.updateState(this.state.game.pgn());
      });

    this.setState(({ history, pieceSquare }) => ({
      fen: this.state.game.fen(),
      history: this.state.game.history(),
      orientation: orientation
    }));
  }

  calcWidth = ({ screenWidth, screenHeight }) => {
    if (!(screenWidth / 1.8 > 940)) {
      return screenWidth / 1.8;
    } else {
      return 940;
    }
  };

  allowDrag = ({ piece, sourceSquare }) => {
    if (
      (this.state.game.turn() === "w" &&
        this.state.game.header().White === retrieveCookie()) ||
      (this.state.game.turn() === "b" &&
        this.state.game.header().Black === retrieveCookie())
    ) {
      if (piece[0] === this.state.game.turn()) {
        return true;
      }
    } else {
      return false;
    }
  };

  onDrop = ({ sourceSquare, targetSquare }) => {
    this.state.game.header("White", this.props.hostplayerid);
    this.state.game.header("Black", this.props.playerid);

    let move = this.state.game.move({
      from: sourceSquare,
      to: targetSquare
    });

    //if move is valid
    if (move == null) return;

    if (this.state.game.pgn() !== null) {
      this.updateState(this.state.game.pgn());
    }
    this.setState(({ history, pieceSquare }) => ({
      fen: this.state.game.fen(),
      history: this.state.game.history()
    }));
  };

  render() {
    return this.props.children({
      position: this.state.game.fen(),
      calcWidth: this.calcWidth,
      onDrop: this.onDrop,
      orientation: this.state.orientation,
      allowDrag: this.allowDrag
    });
  }
}

export default class ChessBoard extends Component {
  render() {
    return (
      <div>
        <Board
          gamesessionid={this.props.gameid}
          hostplayerid={this.props.hostid}
          playerid={this.props.playerid}
          url={this.props.gameid}
        >
          {({
            position,
            calcWidth,
            currentSquare,
            onDrop,
            orientation,
            allowDrag
          }) => (
            <Chessboard
              id="standard"
              orientation={orientation}
              position={position}
              calcWidth={calcWidth}
              onDrop={onDrop}
              allowDrag={allowDrag}
              boardStyle={{
                margin: "auto",
                borderRadius: "5px",
                boxShadow: "0 5px 15px rgba(0, 0, 0, 0.5)"
              }}
            />
          )}
        </Board>
      </div>
    );
  }
}
