import React, { Component } from "react";
import "./App.css";
import "@fortawesome/fontawesome-free/css/all.min.css";
import "bootstrap-css-only/css/bootstrap.min.css";
import "mdbreact/dist/css/mdb.css";
import { Link } from "react-router-dom";
import cookie from "react-cookies";

const emailRegex = RegExp(
  /^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
);

const formValid = ({ formErrors, ...rest }) => {
  let valid = true;

  // validate form errors being empty
  Object.values(formErrors).forEach(val => {
    val.length > 0 && (valid = false);
  });

  // validate the form was filled out
  Object.values(rest).forEach(val => {
    val === null && (valid = false);
  });

  return valid;
};

export default class Registration extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstname: null,
      lastname: null,
      email: null,
      confirmEmail: null,
      password: null,
      confirmPassword: null,
      userid: null,
      formErrors: {
        firstname: "",
        lastname: "",
        email: "",
        confirmEmail: "",
        password: "",
        confirmPassword: "",
        userid: ""
      }
    };
  }

  handleRouteChange() {
    this.props.history.push("/");
  }

  handleChange = e => {
    e.preventDefault();
    const { name, value } = e.target;
    let formErrors = { ...this.state.formErrors };

    switch (name) {
      case "firstname":
        formErrors.firstname =
          value.length < 40 ? "" : "maximum 40 characaters";
        break;
      case "lastname":
        formErrors.lastname = value.length < 40 ? "" : "maximum 40 characaters";
        break;
      case "email":
        formErrors.email = emailRegex.test(value)
          ? ""
          : "invalid email address";
        break;
      case "password":
        formErrors.password =
          value.length < 8 ? "minimum 8 characaters required" : "";
        break;

      case "confirmPassword":
        formErrors.confirmPassword =
          value === this.state.password ? "" : "Password don't match!";
        break;
      case "userid":
        formErrors.userid = value.length < 40 ? "" : "maximum 40 characaters";
        break;

      default:
        break;
    }

    this.setState({ formErrors, [name]: value }, () => console.log(this.state));
  };

  handleSubmit = e => {
    console.log("submitted");
    e.preventDefault();
    const {
      firstname,
      lastname,
      password,
      confirmPassword,
      email,
      confirmEmail,
      userid
    } = this.state;
    if (password !== confirmPassword) {
      alert("Passwords don't match!");
    } else if (email !== confirmEmail) {
      alert("Emails don't match!");
    } else {
      if (formValid(this.state)) {
        this.handleRouteChange();
        console.log(JSON.stringify(this.state));
        fetch("/users/register", {
          method: "POST",
          credentials: "same-origin",
          body: JSON.stringify(this.state),
          headers: {
            "Content-Type": "application/json"
          }
        })
          .then(res => {
            if (res.status === 200) {
              cookie.save(userid);
              this.handleRouteChange();
            } else {
              const error = new Error(res.error);
              throw error;
            }
          })
          .catch(err => {
            console.error(err);
            alert("unable to register account");
          });
      } else
        alert(
          "You have to fill in all the fields with the correct information!"
        );
    }
  };

  render() {
    const { formErrors } = this.state;

    return (
      <div className="wrapper">
        <div className="form-wrapper">
          <h1 className="light-blue-text">Chess</h1>
          <br />
          <form onSubmit={this.handleSubmit} noValidate>
            <div className="userId">
              <label htmlFor="userid">Username</label>
              <input
                className={formErrors.userid.length > 0 ? "error" : null}
                placeholder="User Name"
                type="text"
                name="userid"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.userid.length > 0 && (
                <span className="errorMessage">{formErrors.userid}</span>
              )}
            </div>
            <br />

            <div className="firstName">
              <label htmlFor="firstname">First Name</label>
              <input
                className={formErrors.firstname.length > 0 ? "error" : null}
                placeholder="First Name"
                type="text"
                name="firstname"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.firstname.length > 0 && (
                <span className="errorMessage">{formErrors.firstname}</span>
              )}
            </div>
            <div className="lastName">
              <label htmlFor="lastname">Last Name</label>
              <input
                className={formErrors.lastname.length > 0 ? "error" : null}
                placeholder="Last name"
                type="text"
                name="lastname"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.lastname.length > 0 && (
                <span className="errorMessage">{formErrors.lastname}</span>
              )}
            </div>
            <div className="email">
              <label htmlFor="email">Email</label>
              <input
                className={formErrors.email.length > 0 ? "error" : null}
                placeholder="Email"
                type="email"
                name="email"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.email.length > 0 && (
                <span className="errorMessage">{formErrors.email}</span>
              )}
            </div>

            <div className="confirmEmail">
              <label htmlFor="email">Confirm Email</label>
              <input
                className={formErrors.confirmEmail.length > 0 ? "error" : null}
                placeholder="Confirm Email"
                type="email"
                name="confirmEmail"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.confirmEmail.length > 0 && (
                <span className="errorMessage">{formErrors.confirmEmail}</span>
              )}
            </div>

            <div className="password">
              <label htmlFor="password">Password</label>
              <input
                className={formErrors.password.length > 0 ? "error" : null}
                placeholder="Password"
                type="password"
                name="password"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.password.length > 0 && (
                <span className="errorMessage">{formErrors.password}</span>
              )}
            </div>

            <div className="confirmPassword">
              <label htmlFor="password">Confirm Password</label>
              <input
                className={
                  formErrors.confirmPassword.length > 0 ? "error" : null
                }
                placeholder="Confirm Password"
                type="password"
                name="confirmPassword"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.confirmPassword > 0 && (
                <span className="errorMessage">
                  {formErrors.confirmPassword}
                </span>
              )}
            </div>

            <div className="createAccount">
              <button type="submit">Create Account</button>
              <Link to="/">Already Have an Account?</Link>
            </div>
          </form>
        </div>
      </div>
    );
  }
}
