import React from "react";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import Timer from "react-compound-timer";

function GameControls() {
  //forfeit(){
  // var text = confirm("Are you sure to forfeit this game?");
  // if(text == true){
  //  close(); //close current tab when click yes on forfeit
  // what is the logic when a player closes current tab?
  // do we need to add that or we already have it?
  // }
  //}

  return (
    <div className="gamecontrols">
      <Row>
        <Col className="justify-content-start">
          <Button className="btn-lg">
            <Timer initialTime={0} direction="backward">
              {() => (
                <React.Fragment>
                  <Timer.Minutes />
                  :
                  <Timer.Seconds />
                </React.Fragment>
              )}
            </Timer>
          </Button>
          <Button className="btn-success btn-lg">Accept</Button>
        </Col>
        <Col className="justify-content-end">
          <Button className="btn-danger" type="submit">
            Forfeit Game
          </Button>
        </Col>
      </Row>
    </div>
  );
}

export default GameControls;
